import React from "react";
import ReactDOM from "react-dom";

import "./styles.css";
import logo from "./images/logo.png";
import data from "./data copy.json";

function CharacterCard({character, onClick}) {

  return (
    <div
      className="CharacterCard"
      style={{ backgroundImage: `url(${character.image})` }}
    >
      <button onClick={onClick} className="CharacterCard__name-container text-truncate text-center">
        {character.name}
      </button>
    </div>
  );
}

class Contacts extends React.Component{
  render(){
    return(
      <ul className="row People">
        {this.props.data.results.map(character => (
          <li className="col-6 col-md-3" key={character.id}>
            <CharacterCard character={character} onClick={this.props.onClick}/>
          </li>
        ))}
      </ul>
    )
  }
}

class App extends React.Component {
  state = {
    loading: true,
    error: null,
    actual: "nadie",
    data: {
      info: {},
      results: []
    },
    nextPage: 1
  };

  componentDidMount() {
    this.fetchCharacters();
  }

  onClick(name){
    this.setState({actual:name})
  }

  fetchCharacters = async () => {
    this.setState({ loading: true, error: null });

    try {
      const response = await fetch(
        `https://rickandmortyapi.com/api/character/?page=${this.state.nextPage}`
      );
      //const data = await response.json();

      this.setState({
        loading: false,
        data: {
          info: data.info,
          results: [].concat(this.state.data.results, data.results)
        },
        nextPage: this.state.nextPage + 1
      });
    } catch (error) {
      this.setState({ loading: false, error: error });
    }
  };

  render() {
    if (this.state.error) {
      return "Error!";
    }

    return (
      <div className="container">
        <div className="App">
          <img className="Logo" src={logo} alt="Rick y Morty" />
          <div className="row">
            <Contacts data={this.state.data} onClick={this.onClick}/>
            <div className="Chat">{this.state.actual}</div>
          </div>
          {this.state.loading && <p className="text-center">Loading...</p>}

          {!this.state.loading && this.state.data.info.next && (
            <button onClick={() => this.fetchCharacters()}>Load More</button>
          )}
        </div>
      </div>
    );
  }
}

const rootElement = document.getElementById("root");
ReactDOM.render(<App />, rootElement);
